	public class Devzone : Zone
	{
		public Devzone()
		{
			var z = Testzone;

 		    Name		= "Devzone";
 			Genesis		= "";
 			Father0IP	= z.Father0IP;
			Initials	= z.Initials;
		}
	}
}
