using System;
using System.IO;
using System.Linq;
using System.Net;
using System.Text;
using Nethereum.Signer;

namespace Uccs.Net
{
	public class Zone : ICloneable
	{
		public const string		TestPrefix	= "Testzone";

		public string			Name;
		public string			Genesis;	
		public IPAddress[]		Initials;
 		public Cryptography		Cryptography									= Cryptography.Ethereum;
 		public Chain			EthereumNetwork									= Chain.Sepolia;	
		public bool				PoW												= false;
		public bool				Auctions										= false;
		public int				CommitLength									= 1000;
		public int				ExternalVerificationDurationLimit				= 100;
		public Money			BailMin											= 1000;
		public Money			DomainRankCheckFee								= 5;
		public int				MembersLimit									= 1000;
		public Money			ExeunitMinFee									= 0.001;
		public long				TargetBaseGrowth								= 100L*1024*1024*1024;
		public Money			RentPerBytePerDayMinimum						= 0.000_000_01;
		public int				TransactionsPerRoundLimit						= 5_000; /// for 5000 tx/sec signature recovering
		public int				TransactionsPerVoteAllowableOverflowMuliplier	= 10;
		public int				TransactionsFeeOverflowFactor					= 10;
		public int				OperationsPerTransactionLimit					= 100;
		public int				OperationsPerRoundLimit							=> TransactionsPerRoundLimit * OperationsPerTransactionLimit;

		public AccountAddress	God = AccountAddress.Parse("0xffff50e1605b6f302850694291eb0e688ef15677");
		public AccountAddress	Father0 = AccountAddress.Parse("0x000038a7a3cb80ec769c632b7b3e43525547ecd1");
		public IPAddress		Father0IP;

		public static Zone		OfficialByName(string name) => Official.First(i => i.Name == name);
		public bool				IsTest => Name.StartsWith(TestPrefix);

		public ushort Port	{
								get
								{
									if(this == Zone.Devzone)
										return 3899;
									else if(IsTest)
										return (ushort)(3800 + ushort.Parse(Name.Substring(TestPrefix.Length)));
									else
										return 3800;
								}
							}

 		public static readonly Zone			Localzone = new Localzone();
 		public static readonly Zone			Testzone1 = new Testzone1();
 		public static readonly Zone			Devzone = new Devzone();
		public static readonly Zone			Mainzone = null;
		public static readonly Zone[]		Official = {Localzone, Devzone, Testzone1};

		public Zone()
		{
		}

		public override string ToString()
		{
			return Name;
		}

		public object Clone()
		{
			var z = new Zone();

			z.Name = 			Name;
			z.EthereumNetwork = EthereumNetwork;
			z.Cryptography = 	Cryptography;
			z.Genesis = 		Genesis;
			z.Father0IP = 		Father0IP;
			z.Initials = 		Initials;
			z.BailMin = 		BailMin;
			z.PoW = 			PoW;		
			z.CommitLength = 	CommitLength;
			
			z.God = 			God;
			z.Father0 = 		Father0;
			
			return z;
		}

		public void Save(string directory)
		{
			var d = new XonDocument(new XonTextValueSerializator());

			d.Add("Genesis").Value			= Genesis;
			d.Add("Cryptography").Value		= Cryptography.GetType().Name;
			d.Add("EthereumNetwork").Value	= EthereumNetwork.ToString();
			d.Add("GenesisIP").Value		= Father0IP.ToString();
			d.Add("Initials").Value			= string.Join(' ', Initials.AsEnumerable());

			using(var s = File.Create(Path.Join(directory, Name + ".zone")))
				d.Save(new XonTextWriter(s, Encoding.UTF8));
		}

		public void Load(string path)
		{
			var d = new XonDocument(File.ReadAllText(path));

			Name = Path.GetFileNameWithoutExtension(path);

			Genesis			= d.Get<string>("Genesis");
			Cryptography	= Activator.CreateInstance(typeof(NoCryptography).Assembly.FullName, typeof(NoCryptography).Namespace + '.' + d.Get<string>("Cryptography")).Unwrap() as Uccs.Net.Cryptography;
			EthereumNetwork	= Enum.Parse<Chain>(d.Get<string>("EthereumNetwork"));
			Father0IP		= IPAddress.Parse(d.Get<string>("GenesisIP"));
			Initials		= d.Get<string>("Initials").Split(' ').Select(i => IPAddress.Parse(i)).ToArray();
		}
	}

	public class Localzone : Zone
	{	
		public Localzone()
		{
			Name				= "Localzone";
			Genesis				= "";
			Father0IP			= new IPAddress(new byte[] {127, 0, 0, 100});
			Cryptography		= Cryptography.No;
			PoW					= false;
			Auctions			= true;
			CommitLength		= 100;
			BailMin				= 0.000_000_001;

			Initials			= Enumerable.Range(100, 16).Select(i => new IPAddress(new byte[] {127, 0, 0, (byte)i})).ToArray();
		}
	}

