	public class Devzone : Zone
	{
		public Devzone()
		{
			var z = Testzone1;

 		    Name		= "Devzone";
 			Genesis		= "";
 			Father0IP	= z.Father0IP;
			Initials	= z.Initials;
		}
	}
}
