		public ushort ScopeBasePort => Scope switch	{
														ZoneScope.Local => 100, 
														ZoneScope.Developer  => 200, 
														ZoneScope.Test  => 300 
													};

		public ushort Port => (ushort)(BasePort + ScopeBasePort);
	}

	public class Interzone : Zone
	{
		public override ushort				BasePort => 1000;
		public override	ZoneScope			Scope => throw new NotImplementedException();

		public static readonly Interzone	Local = new LocalInterzone();
 		public static readonly Interzone	Test = new TestInterzone();
 		public static readonly Interzone	Developer = new DeveloperInterzone();
		public static readonly Interzone	Main = null;
		public static readonly Interzone[]	Official = {Local, Developer, Test};

		public static Interzone				ByName(string name) => Official.First(i => i.Name == name);
		public static Interzone				Byid(Guid zoneid) => Official.First(i => i.Id == zoneid);

	}

	public class LocalInterzone : Interzone
	{
		public override	ZoneScope	Scope => ZoneScope.Local;

		public LocalInterzone()
		{
			Id			= new Guid("FFFFFFFF-0000-0000-0000-000000001100");
			Initials		= LocalInitials;
		}
	}

	public class DeveloperInterzone : Interzone
	{
		public override	ZoneScope	Scope => ZoneScope.Developer;

		public DeveloperInterzone()
		{
			Id			= new Guid("FFFFFFFF-0000-0000-0000-000000001200");
			Initials		= UOInitials;
		}
	}

	public class TestInterzone : Interzone
	{
		public override	ZoneScope	Scope => ZoneScope.Test;

		public TestInterzone()
		{
			Id			= new Guid("00000000-0000-0000-0000-000000001300");
			Initials		= UOInitials;
		}
	}

	public abstract class McvZone : Zone
	{
		public string				Genesis;	
 		public Cryptography			Cryptography									= Cryptography.Ethereum;
		public int					CommitLength									= 1000;
		public int					ExternalVerificationDurationLimit				= 100;
		public Money				BailMin											= 1000;
		public bool					PoW												= false;
		public int					MembersLimit									= 1000;
		public Money				ExeunitMinFee									= 0.001;
		public long					TargetBaseGrowthPerYear							= 100L*1024*1024*1024;
		public Money				RentPerBytePerDayMinimum						= 0.000_000_01;
		public int					TransactionsPerRoundLimit						= 5_000; /// for 5000 tx/sec signature recovering
		public int					TransactionsPerVoteAllowableOverflowMuliplier	= 10;
		public int					TransactionsFeeOverflowFactor					= 10;
		public int					OperationsPerTransactionLimit					= 100;
		public int					OperationsPerRoundLimit							=> TransactionsPerRoundLimit * OperationsPerTransactionLimit;

		public AccountAddress		God												= AccountAddress.Parse("0xffff50e1605b6f302850694291eb0e688ef15677");
		public AccountAddress		Father0											= AccountAddress.Parse("0x000038a7a3cb80ec769c632b7b3e43525547ecd1");
		public IPAddress			Father0IP;
		public ZoneCreation			Creation;
	}

	public abstract class RdnZone : McvZone
	{
		public override ushort					BasePort => 2000;
 		
		public Chain							EthereumNetwork		= Chain.Sepolia;	
		public bool								Auctions			= false;
		public Money							DomainRankCheckFee	= 5;

 		public static readonly RdnZone			Local = new RdnLocalZone();
 		public static readonly RdnZone			Test = new RdnTestZone();
 		public static readonly RdnZone			Developer = new RdnDevZone();
		public static readonly RdnZone			Main = null;
		public static readonly RdnZone[]		Official = {Local, Developer, Test};

		public static RdnZone					ByName(string name) => Official.First(i => i.Name == name);
		public static RdnZone					ById(Guid zoneid) => Official.First(i => i.Id == zoneid);
		
		public RdnZone()
		{
		}

		public override string ToString()
		{
			return Name;
		}
	}

	public class RdnLocalZone : RdnZone
	{	
		public override	ZoneScope	Scope => ZoneScope.Local;
		
		public RdnLocalZone()
		{
			Id				= new Guid("FFFFFFFF-0000-0000-0000-000000002100");
			Genesis			= "";
			Father0IP		= new IPAddress(new byte[] {127, 0, 0, 100});
			Cryptography	= Cryptography.No;
			PoW				= false;
			Auctions		= true;
			CommitLength	= 100;
			BailMin			= 0.000_000_001;

			Initials		= Enumerable.Range(100, 16).Select(i => new IPAddress(new byte[] {127, 0, 0, (byte)i})).ToArray();
		}
	}

	public class RdnDevZone : RdnZone
	{
		public override	ZoneScope	Scope => ZoneScope.Developer;
	
		public RdnDevZone()
		{
			Id		= new Guid("FFFFFFFF-0000-0000-0000-000000002200");
 			Genesis	= "";
			
			var z = Test;

 			Father0IP	= z.Father0IP;
			Initials	= z.Initials;
		}
	}

	public class RdnTestZone : RdnZone
	{
		public override	ZoneScope	Scope => ZoneScope.Test;
	
		public RdnTestZone()
		{
			Id			= new Guid("00000000-0000-0000-0000-000000002300");
 			Genesis		= "";
 			Father0IP	= IPAddress.Parse("78.47.204.100");
		}
	}
}
