using System;
using System.Collections.Generic;
using System.Linq;
using System.Net;
using System.Text;
using System.Threading.Tasks;
using Nethereum.Signer;

namespace Uccs.Net
{
	public class SimulationZone : Zone
	{
		public SimulationZone()
		{
			Name							= "Simulation";
			Genesis							= ""; 
			GenesisIP						= new IPAddress(new byte[] {127, 0, 0, 100});
			Cryptography					= Cryptography.No;
			EthereumNetwork					= Chain.Goerli;
			TailLength						= 100;
			BailMin							= Money.Min;
			ExternalVerificationDurationLimit = 100;
			PoW								= false;
			MembersLimit					= 1000;
			TransactionsPerRoundLimit		= 30000;
			OperationsPerRoundLimit			= 1_000_000;
			OperationsPerTransactionLimit	= 1000;


			God							= Zone.Testnet1.God;
			Father0						= Zone.Testnet1.Father0;
			
			Initials					= Enumerable.Range(100, 16).Select(i => new IPAddress(new byte[] {127, 0, 0, (byte)i})).ToArray();
		}
	}
}
