	public class Devzone : Zone
	{
		public Devzone()
		{
			var z = Zone.Testzone1;

 		    Name		= "Devzone";
 			Genesis		= "";
 			Father0IP	= z.Father0IP;
			Initials	= z.Initials;
		}
	}
}
